mapper.update:
UPDATE Item
SET productId = #{productId}, image = #{image}, 
description = #{description}, title = #{title}, 
price = #{price}, isSoldout = #{isSoldout}, 
likeCnt = #{likeCnt}, quantity = #{quantity}
WHERE itemId = #{itemId}

mapper.select:
 SELECT QTY AS
		value
		FROM INVENTORY WHERE ITEMID = #{itemId}
